import React, { useState } from 'react';
import Navbar from './components/Navbar';
import SearchBar from './components/SearchBar';
import './App.css';

function App() {
  const [results, setResults] = useState(null); // État pour les résultats

  const handleSearch = (query) => {
    // Simulation de résultats pour le test
    const mockResults = {
      generated_response: "Voici une réponse générée par l'IA.",
      retrieved_documents: ["Document 1", "Document 2", "Document 3"]
    };
    setResults(mockResults);
  };

  return (
    <div className="App">
      <Navbar />
      <div className="content">
        <SearchBar onSearch={handleSearch} />
        {results && (
          <div className="results">
            <h2>Résultat généré :</h2>
            <p>{results.generated_response}</p>
            <h3>Documents associés :</h3>
            <ul>
              {results.retrieved_documents.map((doc, index) => (
                <li key={index}>{doc}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
