import React, { useState } from 'react';
import './App.css';
import SearchBar from './SearchBar';

function App() {
  const [apiResponse, setApiResponse] = useState(''); // Stocker la réponse de l'API
  const [loading, setLoading] = useState(false); // Indicateur de chargement

  const handleSearch = async (query) => {
    setLoading(true); // Activer l'indicateur de chargement
    try {
      const response = await fetch('http://localhost:8000/query', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ question: query }),
      });

      if (!response.ok) {
        throw new Error('Failed to fetch the answer.');
      }

      const data = await response.json();
      setApiResponse(data.answer); // Mettre à jour la réponse API
    } catch (error) {
      console.error(error);
      setApiResponse('An error occurred while fetching the answer.');
    } finally {
      setLoading(false); // Désactiver l'indicateur de chargement
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="logo-container">
          <img src="logo.png" alt="Logo" className="logo" />
          <h1>Your SE</h1>
        </div>
        <SearchBar onSearch={handleSearch} /> {/* Ajout de SearchBar */}
        {loading ? (
          <p className="description">Loading...</p> // Afficher "Loading" pendant le chargement
        ) : (
          <p className="description">{apiResponse}</p> // Afficher la réponse de l'API
        )}
        <div className="buttons-container">
          <button className="action-button">Google Drive</button>
          <button className="action-button">My Docs</button>
        </div>
      </header>
    </div>
  );
}

export default App;
